import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ClientEntity } from 'src/app/models/ClientEntity';
import { ProductService } from 'src/app/services/product.service';

@Component({
  selector: 'app-client-create',
  templateUrl: './client-create.component.html',
  styleUrls: ['./client-create.component.css']
})
export class ClientCreateComponent {

  public createClientForm: FormGroup;

  // instantiate a new client
  public newClient: ClientEntity = new ClientEntity();

  // inject dependencies 
  constructor(private fb: FormBuilder, private productService: ProductService, form: FormGroup) {
    // initialize form 
    this.createClientForm = form;

    // create form 
    this.fb.group({
      "names": ['', Validators.required],
      "lastnames": ['', Validators.required]
    });

  }

  onSubmit() {

    this.newClient = this.createClientForm.get('names')?.value;
    this.newClient = this.createClientForm.get('lastnames')?.value;

    // make request to back-end
    this.productService.create(this.newClient).subscribe({
      next: (res: any) => {
        console.log("Data of client send successfully to back-end: ");
        console.log(res);
      }, 
      error: ( error ) => {
        console.log("Error when trying to send a new client to back-end: ");
        console.log(error);
      }
    })
  }

}
