import { Component } from '@angular/core';
import { ClientEntity } from 'src/app/models/ClientEntity';
import { ProductService } from 'src/app/services/product.service';

@Component({
  selector: 'app-product-list',
  templateUrl: './product-list.component.html',
  styleUrls: ['./product-list.component.css']
})
export class ProductListComponent {

  public clients: ClientEntity[] = [];

  constructor(public productService: ProductService) {


    // make request to back-en
    this.productService.getAll().subscribe({

      next: (res: any) => {
        // set the result to the client list
        this.clients = res;
        // log to console
        console.log("this are the client: ");
        console.log(res);
      },
      error: (error) => {
        console.log("Not posible to get the clients.");
        console.log(error);
      }
    });


  }



}
